mapper.select:
select 

from lottery_issues
where lottery_issues_id = #{lotteryIssuesId,jdbcType=INTEGER}
  and end_bet_time = #{endBetTime,jdbcType=INTEGER}

mapper.delete:
delete from lottery_issues
where lottery_issues_id = #{lotteryIssuesId,jdbcType=INTEGER}
  and end_bet_time = #{endBetTime,jdbcType=INTEGER}
